import React, { useState, useEffect } from 'react';
import { Pencil, Trash2, Plus, Search } from 'lucide-react';

const API_URL = 'http://localhost:3001/api';

const CrudApp = () => {
  // State for managing items and forms
  const [items, setItems] = useState([]);
  const [createFormData, setCreateFormData] = useState({ name: '', description: '' });
  const [updateFormData, setUpdateFormData] = useState({ id: '', name: '', description: '' });
  const [searchId, setSearchId] = useState('');
  const [searchResult, setSearchResult] = useState(null);

  // Fetch all items when component mounts
  useEffect(() => {
    fetchItems();
  }, []);

  // Read operations
  const fetchItems = async () => {
    try {
      const response = await fetch(`${API_URL}/items`);
      const data = await response.json();
      setItems(data);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchId) return;

    try {
      const response = await fetch(`${API_URL}/items/${searchId}`);
      const data = await response.json();
      setSearchResult(data);
    } catch (error) {
      console.error('Error fetching item:', error);
      setSearchResult(null);
    }
  };

  // Create operation
  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${API_URL}/items`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(createFormData),
      });
      await response.json();
      setCreateFormData({ name: '', description: '' });
      fetchItems();
    } catch (error) {
      console.error('Error creating item:', error);
    }
  };

  // Update operation
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await fetch(`${API_URL}/items/${updateFormData.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: updateFormData.name,
          description: updateFormData.description
        }),
      });
      setUpdateFormData({ id: '', name: '', description: '' });
      fetchItems();
    } catch (error) {
      console.error('Error updating item:', error);
    }
  };

  // Delete operation
  const handleDelete = async (id) => {
    try {
      await fetch(`${API_URL}/items/${id}`, {
        method: 'DELETE',
      });
      fetchItems();
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">CRUD Operations</h1>

      {/* Read Section */}
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Read Operations</h2>

        {/* Search by ID */}
        <div className="mb-4 p-4 bg-gray-100 rounded-lg">
          <h3 className="text-lg font-medium mb-2">Search by ID</h3>
          <form onSubmit={handleSearch} className="flex gap-2">
            <input
              type="text"
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
              placeholder="Enter item ID"
              className="flex-1 p-2 border rounded"
            />
            <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
              <Search className="w-4 h-4" />
            </button>
          </form>
          {searchResult && (
            <div className="mt-2 p-2 bg-white rounded">
              <p><strong>ID:</strong> {searchResult.id}</p>
              <p><strong>Name:</strong> {searchResult.name}</p>
              <p><strong>Description:</strong> {searchResult.description}</p>
            </div>
          )}
        </div>

        {/* All Items List */}
        <div className="bg-gray-100 p-4 rounded-lg">
          <h3 className="text-lg font-medium mb-2">All Items</h3>
          <div className="space-y-2">
            {items.map((item) => (
              <div key={item.id} className="bg-white p-2 rounded flex justify-between items-center">
                <div>
                  <p><strong>ID: {item.id}</strong></p>
                  <p>Name: {item.name}</p>
                  <p>Description: {item.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Create Section */}
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Create New Item</h2>
        <form onSubmit={handleCreate} className="p-4 bg-gray-100 rounded-lg">
          <div className="mb-4">
            <input
              type="text"
              value={createFormData.name}
              onChange={(e) => setCreateFormData({ ...createFormData, name: e.target.value })}
              placeholder="Name"
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <div className="mb-4">
            <input
              type="text"
              value={createFormData.description}
              onChange={(e) => setCreateFormData({ ...createFormData, description: e.target.value })}
              placeholder="Description"
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 flex items-center">
            <Plus className="w-4 h-4 mr-2" />
            Create Item
          </button>
        </form>
      </section>

      {/* Update Section */}
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Update Item</h2>
        <form onSubmit={handleUpdate} className="p-4 bg-gray-100 rounded-lg">
          <div className="mb-4">
            <input
              type="text"
              value={updateFormData.id}
              onChange={(e) => setUpdateFormData({ ...updateFormData, id: e.target.value })}
              placeholder="Item ID"
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <div className="mb-4">
            <input
              type="text"
              value={updateFormData.name}
              onChange={(e) => setUpdateFormData({ ...updateFormData, name: e.target.value })}
              placeholder="New Name"
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <div className="mb-4">
            <input
              type="text"
              value={updateFormData.description}
              onChange={(e) => setUpdateFormData({ ...updateFormData, description: e.target.value })}
              placeholder="New Description"
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <button type="submit" className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600 flex items-center">
            <Pencil className="w-4 h-4 mr-2" />
            Update Item
          </button>
        </form>
      </section>

      {/* Delete Section */}
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Delete Item</h2>
        <div className="p-4 bg-gray-100 rounded-lg">
          <p className="mb-4">Select an item to delete from the list below:</p>
          <div className="space-y-2">
            {items.map((item) => (
              <div key={item.id} className="bg-white p-2 rounded flex justify-between items-center">
                <div>
                  <p><strong>ID: {item.id}</strong></p>
                  <p>Name: {item.name}</p>
                </div>
                <button
                  onClick={() => handleDelete(item.id)}
                  className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 flex items-center"
                >
                  <Trash2 className="w-4 h-4 mr-2" />
                  Delete
                </button>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default CrudApp;
