// App.js

// Import necessary dependencies - React hooks and icons
import React, { useState, useEffect } from 'react';
import { Pencil, Trash2, Plus, Search } from 'lucide-react';
// Import our custom styles
import './App.css';

// Define the API URL for our backend server
const API_URL = 'http://localhost:3001/api';

const CrudApp = () => {
  // State management for different aspects of our application
  const [items, setItems] = useState([]); // Stores all items
  const [createFormData, setCreateFormData] = useState({ name: '', description: '' }); // For creating new items
  const [updateFormData, setUpdateFormData] = useState({ id: '', name: '', description: '' }); // For updating items
  const [searchId, setSearchId] = useState(''); // For searching items
  const [searchResult, setSearchResult] = useState(null); // Stores search results

  // Fetch all items when component mounts
  useEffect(() => {
    fetchItems();
  }, []);

  // Function to fetch all items from the API
  const fetchItems = async () => {
    try {
      const response = await fetch(`${API_URL}/items`);
      const data = await response.json();
      setItems(data);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  // Function to handle item search
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchId) return;

    try {
      const response = await fetch(`${API_URL}/items/${searchId}`);
      const data = await response.json();
      setSearchResult(data);
    } catch (error) {
      console.error('Error fetching item:', error);
      setSearchResult(null);
    }
  };

  // Function to create a new item
  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${API_URL}/items`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(createFormData),
      });
      await response.json();
      setCreateFormData({ name: '', description: '' }); // Reset form
      fetchItems(); // Refresh the items list
    } catch (error) {
      console.error('Error creating item:', error);
    }
  };

  // Function to update an existing item
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await fetch(`${API_URL}/items/${updateFormData.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: updateFormData.name,
          description: updateFormData.description
        }),
      });
      setUpdateFormData({ id: '', name: '', description: '' }); // Reset form
      fetchItems(); // Refresh the items list
    } catch (error) {
      console.error('Error updating item:', error);
    }
  };

  // Function to delete an item
  const handleDelete = async (id) => {
    try {
      await fetch(`${API_URL}/items/${id}`, {
        method: 'DELETE',
      });
      fetchItems(); // Refresh the items list
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  return (
    <div className="crud-container">
      <h1 className="text-4xl font-bold text-center mb-8">CRUD Operations</h1>

      <div className="operations-grid">
        {/* Read Operation Card */}
        <div className="operation-card">
          <h2 className="operation-title">Read Operations</h2>

          {/* Search Form */}
          <form onSubmit={handleSearch} className="mb-4">
            <input
              type="text"
              value={searchId}
              onChange={(e) => setSearchId(e.target.value)}
              placeholder="Enter item ID"
              className="form-input"
            />
            <button type="submit" className="button bg-blue-500 text-white hover:bg-blue-600">
              <Search className="w-4 h-4" />
              Search
            </button>
          </form>

          {/* Display search result if exists */}
          {searchResult && (
            <div className="search-result">
              <p><strong>ID:</strong> {searchResult.id}</p>
              <p><strong>Name:</strong> {searchResult.name}</p>
              <p><strong>Description:</strong> {searchResult.description}</p>
            </div>
          )}

          {/* Display all items */}
          <div className="items-list">
            {items.map((item) => (
              <div key={item.id} className="item-card">
                <p><strong>ID:</strong> {item.id}</p>
                <p><strong>Name:</strong> {item.name}</p>
                <p><strong>Description:</strong> {item.description}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Create Operation Card */}
        <div className="operation-card">
          <h2 className="operation-title">Create New Item</h2>
          <form onSubmit={handleCreate} className="h-full flex flex-col">
            <input
              type="text"
              value={createFormData.name}
              onChange={(e) => setCreateFormData({ ...createFormData, name: e.target.value })}
              placeholder="Name"
              className="form-input"
              required
            />
            <input
              type="text"
              value={createFormData.description}
              onChange={(e) => setCreateFormData({ ...createFormData, description: e.target.value })}
              placeholder="Description"
              className="form-input"
              required
            />
            <button type="submit" className="button bg-green-500 text-white hover:bg-green-600 mt-auto">
              <Plus className="w-4 h-4" />
              Create Item
            </button>
          </form>
        </div>

        {/* Update Operation Card */}
        <div className="operation-card">
          <h2 className="operation-title">Update Item</h2>
          <form onSubmit={handleUpdate}>
            <input
              type="text"
              value={updateFormData.id}
              onChange={(e) => setUpdateFormData({ ...updateFormData, id: e.target.value })}
              placeholder="Item ID"
              className="form-input"
              required
            />
            <input
              type="text"
              value={updateFormData.name}
              onChange={(e) => setUpdateFormData({ ...updateFormData, name: e.target.value })}
              placeholder="New Name"
              className="form-input"
              required
            />
            <input
              type="text"
              value={updateFormData.description}
              onChange={(e) => setUpdateFormData({ ...updateFormData, description: e.target.value })}
              placeholder="New Description"
              className="form-input"
              required
            />
            <button type="submit" className="button bg-yellow-500 text-white hover:bg-yellow-600">
              <Pencil className="w-4 h-4" />
              Update Item
            </button>
          </form>
        </div>

        {/* Delete Operation Card */}
        <div className="operation-card">
          <h2 className="operation-title">Delete Item</h2>
          <div className="items-list">
            {items.map((item) => (
              <div key={item.id} className="item-card flex justify-between items-center">
                <div>
                  <p><strong>ID:</strong> {item.id}</p>
                  <p><strong>Name:</strong> {item.name}</p>
                </div>
                <button
                  onClick={() => handleDelete(item.id)}
                  className="button bg-red-500 text-white hover:bg-red-600 w-auto"
                >
                  <Trash2 className="w-4 h-4" />
                  Delete
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CrudApp;
